import math
import collections
class Solution:
    """
    Given array nums = [-1, 0, 1, 2, -1, -4],  A solution set is:
    [
        [-1, 0, 1],
        [-1, -1, 2]
    ]
    """

    def threeSum(self, nums):
        """
        :type nums: List[int]
        :rtype: List[List[int]]

        Time: O(N^2)
        Space :O(1) We skip output space complexity by convention, so 'result' doesn't count.
        """
        nums.sort()
        res = []
        for i in range(len(nums)):
            if nums[i] > 0:
                return res
            if (i > 0 and nums[i] == nums[i - 1]):
                continue  # Skip duplicates.
            target = 0 - nums[i]
            start = i + 1
            end = len(nums) - 1
            while start < end:
                if nums[start] + nums[end] == target:
                    res.append([nums[i], nums[start], nums[end]])
                    while (start < end and nums[start + 1] == nums[start]):
                        start += 1 #Skip duplicates when adjusting start after a match is found.
                    while (end > start and nums[end - 1] == nums[end]):
                        end -= 1 #Skip duplicates when adjusting end after a match is found.
                if nums[start] + nums[end] < target:
                    start += 1 #No need to skip duplicates because if it's not a match, further duplicates will still not match
                else:
                    end -= 1 #No need to skip duplicates because if it's not a match, further duplicates will still not match
        return res

    def threeSum_no_sorting(self, nums):
        """
        :type nums: List[int]
        :rtype: List[List[int]]
        """
        res = []
        for i, n in enumerate(nums):
            twoSum = self.twoSum(nums[:i] + nums[i + 1:], 0 - n)
            if twoSum:
                for x, y in twoSum:
                    candidate = [n, x, y]
                    candidate_set = set(candidate)
                    T = collections.Counter(candidate)
                    if any(T == collections.Counter(x) for x in res):
                        continue
                    else:
                        res.append(candidate)
                    # for s in res:
                    #     if set(s) == candidate_set:
                    #         break


        return res

    def twoSum(self, nums, target):
        """
        Time: O(N)
        Space: O(N)
        """
        res = []
        s = set()
        seen = set()
        for n in nums:
            remainder = target - n
            if remainder in s and remainder not in seen:
                res.append([n, target - n])
                seen.add(n)
                seen.add(target - n)
            s.add(n)
        return res



    def threeSumClosest(self, nums, target):
        """
        :type nums: List[int]
        :type target: int
        :rtype: int
        """
        nums.sort()
        closest = float('inf')
        for i in range(len(nums) - 2):
            if i < 1 or nums[i] != nums[i - 1]:  # Keep advancing i until it's not repeated, and use i as the 'first' member of triplet
                start = i + 1
                end = len(nums) - 1
                while start < end:
                    tripleSum = nums[i] + nums[start] + nums[end]
                    if tripleSum == target:
                        return tripleSum
                    elif tripleSum > target:
                        end -= 1
                    else:
                        start += 1
                    if math.fabs(tripleSum - target) < math.fabs(closest - target):
                        closest = tripleSum
        return closest

    def FourSum(self, nums, target):
        def NSumSorted(nums, target, N, previousResults, finalResults):
            if (len(nums)<N or N<2 or nums[0]*N > target or nums[-1]*N < target):
                return []
            if N==2: #Base Case
                r = len(nums)-1
                l = 0
                while (l<r):
                    sum = nums[l] + nums[r]
                    if sum == target:
                        finalResults.append(previousResults + [nums[l],nums[r]])
                        l+=1
                        r-=1
                        while (l<r and nums[l]==nums[l-1]):
                            l+=1
                        while (l<r and nums[r]==nums[r+1]):
                            r-=1
                    elif sum<target:
                        l+=1
                    else:
                        r-=1
            else: #Recursive case
                for i in range(len(nums)-N+1): #Ensure that there are at least N elements left in the end.
                    if i < 1 or nums[i]!=nums[i-1]:
                        #Call NSumSorted passing in rest of the array after first element, with target as remainder of first element
                        NSumSorted(nums[i+1:],target-nums[i],N-1,previousResults+[nums[i]],finalResults)

        nums.sort()
        finalResults = []
        NSumSorted(nums, target, 4, [], finalResults)
        return finalResults


