import math

class Solution:
    def threeSum(self, nums, target):
        """
        :type nums: List[int]
        :rtype: List[List[int]]
        """
        nums.sort()
        allSums = []
        for i in range(len(nums)-2):
            if i<1 or nums[i]!=nums[i-1]: #Keep advancing i until it's not repeated, and use i as the 'first' member of triplet
                start = i+1
                end = len(nums)-1
                while start<end:
                    tripleSum = nums[i] + nums[start] + nums[end]
                    if tripleSum == target:
                        #Once found the pair that'll add to target alongwith the first number, move both start and end towards each other
                        allSums.append([nums[i], nums[start], nums[end]])

                        start += 1
                        while nums[start]==nums[start - 1] and start<end:
                            start += 1 #Keep moving start index towards the end as long as it's a repeat and its before end

                        end -= 1
                        while nums[end] == nums[end + 1] and end > start:
                            end -= 1 #Keep moving end index towards the start as long as it's a repeat and its after start
                    elif tripleSum > target:
                        end -= 1
                        while nums[end] == nums[end + 1] and start < end:
                            end -= 1 #Keep moving end index towards the start as long as it's a repeat and its after start
                    else:
                        start += 1
                        while (nums[start] == nums[start - 1] and start < end):
                            start += 1  # Keep moving start index towards the end as long as it's a repeat and its before end

        return allSums

    def threeSumClosest(self, nums, target):
        """
        :type nums: List[int]
        :type target: int
        :rtype: int
        """
        nums.sort()
        closest = float('inf')
        for i in range(len(nums) - 2):
            if i < 1 or nums[i] != nums[i - 1]:  # Keep advancing i until it's not repeated, and use i as the 'first' member of triplet
                start = i + 1
                end = len(nums) - 1
                while start < end:
                    tripleSum = nums[i] + nums[start] + nums[end]
                    if tripleSum == target:
                        return tripleSum
                    elif tripleSum > target:
                        end -= 1
                    else:
                        start += 1
                    if math.fabs(tripleSum - target) < math.fabs(closest - target):
                        closest = tripleSum
        return closest

    def FourSum(self, nums, target):
        def NSumSorted(nums, target, N, previousResults, finalResults):
            if (len(nums)<N or N<2 or nums[0]*N > target or nums[-1]*N < target):
                return []
            if N==2: #Base Case
                r = len(nums)-1
                l = 0
                while (l<r):
                    sum = nums[l] + nums[r]
                    if sum == target:
                        finalResults.append(previousResults + [nums[l],nums[r]])
                        l+=1
                        r-=1
                        while (l<r and nums[l]==nums[l-1]):
                            l+=1
                        while (l<r and nums[r]==nums[r+1]):
                            r-=1
                    elif sum<target:
                        l+=1
                    else:
                        r-=1
            else: #Recursive case
                for i in range(len(nums)-N+1): #Ensure that there are at least N elements left in the end.
                    if i < 1 or nums[i]!=nums[i-1]:
                        #Call NSumSorted passing in rest of the array after first element, with target as remainder of first element
                        NSumSorted(nums[i+1:],target-nums[i],N-1,previousResults+[nums[i]],finalResults)

        nums.sort()
        finalResults = []
        NSumSorted(nums, target, 4, [], finalResults)
        return finalResults


